function palindrome(str) {
  // Good luck!
  let checkStr = '';
  str = str.replace(/\W/g, '');
  str = str.replace(/_/, '');
  str = str.replace(/,/, '');

  str = str.toLowerCase();
  checkStr = [...str].reverse().join('');
  if (checkStr == str){
    console.log(true);
    return true;
  } else{
    return false;
  }
}



